function theme_change() {
    // 
    var button = document.getElementById('theme_change');
    var pattern = new RegExp('', 'i');
    if (pattern.test(button.innerHTML)){
        button.innerHTML = "";
    } else{
        button.innerHTML = "";
    }
    // 
    document.body.classList.toggle('dark-theme');
};
