stance(mobile, unicode): mobile = mobile.encode(&apos;utf-8&apos;) if isinstance(created_at, unicode): created_at = created_at.encode(&apos;utf-8&apos;) if isinstance(member_created_at, unicode): member_created_at = member_created_at.encode(&apos;utf-8&apos;) if isinstance(name, unicode): name = name.encode(&apos;utf-8&apos;) if isinstance(mobile, unicode): mobile = mobile.encode(&apos;utf-8&apos;) # insert_sql = &quot;&quot;&quot; # insert into client(weixin_fans_id, fans_id, created_at, gender, is_member, trade_count, show_name, name, yz_uid, points, mobile, member_created_at) # VALUES(%s, %s, &quot;%s&quot;, &quot;%s&quot;, %s, %s, &quot;%s&quot;, &quot;%s&quot;, %s, %s, &quot;%s&quot;, &quot;%s&quot;); # &quot;&quot;&quot; % (weixin_fans_id, fans_id, created_at, gender, is_member, trade_count, show_name, name, yz_uid, points, mobile, member_created_at) insert_sql = &apos;insert into client_new(created_at, is_member, name, show_name, mobile, member_created_at) VALUES(%s, %s, %s, %s, %s, %s);&apos; cursor = self.conn.cursor() cursor.execute(insert_sql, (created_at, is_member, name, show_name, mobile, member_created_at)) self.conn.commit() cursor.close() def deal_client_by_token(self, token, store): &quot;&quot;&quot; main method by token 设置一个起始时间，每次递增加1月进行数据获取，以此方式进行all data获取 :param token: :param store: db or excel :return: &quot;&quot;&quot; all_datas = list() end_time = self.START_DATETIME while True: start_time = end_time if start_time == self.END_DATETIME: break end_time = self.deal_time(start_time, 1, &apos;add&apos;, &apos;hours&apos;) # 去掉脏数据时间段 if start_time in self.EXCLUDE_DATETIME: print &apos;【%s】is exclude datetime&apos; % start_time continue payload = &#123; # &quot;is_member&quot;: 1, # 是否会员 &quot;created_at_start&quot;: self._transfer_to_time(start_time, 2), # 成为客户的时间 &quot;created_at_end&quot;: self._transfer_to_time(end_time, 2) # 成为客户的时间 &#125; headers = &#123; &apos;Content-Type&apos;: &quot;multipart/form-data&quot;, &#125; real_url = &apos;%s?access_token=%s&apos; % (self.YOUZAN_CLIENT_URL, token) data_resp = requests.post(url=real_url, # headers=headers, data=payload) if data_resp.status_code != 200: print &apos;【%s】client请求失败&apos; % start_time continue resp_json_datas = data_resp.json() if not resp_json_datas: print &apos;【%s】client无json数据&apos; % start_time continue code = resp_json_datas.get(&apos;code&apos;) if code != 200: print &apos;【%s】client code is error: %s&apos; % (start_time, code) continue total = resp_json_datas.get(&apos;data&apos;).get(&apos;total&apos;) if total &lt; 1: print &apos;【%s】client json数据total: %s&apos; % (start_time, total) continue # 特别说2 = 1 + 1，有一个page页面的1，还有一个range的1 page = int(total / 50) + 2 self.TOTAL = self.TOTAL + total print &apos;%s - %s total: %s, page: %s&apos; \ % (start_time, end_time, total, (page - 1)) for i in range(1, page, 1): print &apos;===========%s&apos; % i sub_payload = &#123; &quot;created_at_start&quot;: self._transfer_to_time(start_time, 2), # 成为客户的时间 &quot;created_at_end&quot;: self._transfer_to_time(end_time, 2), # 成为客户的时间 &quot;page_size&quot;: self.MAX_PAGE_SIZE # 每页数量，最多支持50个 &#125; sub_data_resp = requests.post(url=real_url, data=sub_payload) sub_resp_json_datas = sub_data_resp.json() datas = sub_resp_json_datas.get(&apos;data&apos;).get(&apos;record_list&apos;) for data in datas: if not data: continue self._deal_client_to_db(data) if store == &apos;db&apos; \ else all_datas.append(data) if store == &apos;excel&apos;: self._deal_client_to_excel(all_datas) def run(self, _type=1): store = &apos;db&apos; if _type == 1 else &apos;excel&apos; is_ok, message = self.get_token() if not is_ok: print &apos;YouZan token获取失败：【%s】&apos; % message os._exit() print &apos;token: %s&apos; % message self.deal_client_by_token(message, store) if store == &apos;db&apos;: youzan.conn.close() print &apos;===================end: %s&apos; % self.TOTALif __name__ == &apos;__main__&apos;: youzan = YOUZAN_Client() youzan.run(_type=2)
